package collections;

import java.util.Scanner;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l = 7;
		
		for(int i = 1; i <= l; i++)
		{			
			for(int j = 1; j <= i; j++)
			{
				System.out.print("* ");
				
			}
			System.out.println();
			
		}
			
		}

}
